    private void jButton1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton1MouseClicked
        String nome = campoNome.getText();
        String cognome = campoCognome.getText();
        String eta = "" + campoEta.getValue();
        String genere = (String) campoGenere.getSelectedItem();
        String password = campoPassword.getText();
        String nome_utente = campoNomeUtente.getText();
        String msg = amministrazione.inserisciDottore(nome, cognome, eta, genere, nome_utente, password);
        JOptionPane.showMessageDialog(null, msg, "Nuovo dottore", JOptionPane.INFORMATION_MESSAGE);
    }//GEN-LAST:event_jButton1MouseClicked

    private void campoEtaStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_campoEtaStateChanged
       int eta = (int) campoEta.getValue();
       if(eta < 18) {
           campoEta.setValue(18);
       }
    }//GEN-LAST:event_campoEtaStateChanged

    private void jButton2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton2MouseClicked
        String nome_utente = campoElimina.getText();
        String tipo = (String) tipologia.getSelectedItem();
        String msg = amministrazione.eliminaUtente(nome_utente, tipo);
        JOptionPane.showMessageDialog(null, msg, "Eliminazione", JOptionPane.INFORMATION_MESSAGE);
    }//GEN-LAST:event_jButton2MouseClicked

